function displayAlert(e,i,t){const c={info:{icon:"bi-info-circle"},success:{icon:"bi-patch-check"},warning:{icon:"bi-exclamation-triangle"},danger:{icon:"bi-x-octagon"}};document.querySelectorAll(e+" div.alert").forEach(e=>{e.remove()});let n=document.createElement("div");n.classList.add("alert","alert-dismissible","fade","show","alert-"+i),n.innerHTML='<i class="bi '+(c[i]?c[i].icon:"")+'"></i> '+t+'<button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button></div>',document.querySelector(e).prepend(n)}